CREATE VIEW dbo.vw_SinavDersOturum
AS
SELECT        TOP (100) PERCENT S.ID_SINAV, SD.BOLUMNO, SD.ID_DERS, SD.ID_SINAVDERS, SOD.OTURUM
FROM            dbo.Sinav AS S INNER JOIN
                         dbo.SinavDers AS SD ON SD.ID_SINAV = S.ID_SINAV INNER JOIN
                         dbo.SinavOptikDers AS SOD ON SOD.ID_SINAVDERS = SD.ID_SINAVDERS
WHERE        (S.AKTIF = 1)
