													WHEN M.DIN_MUAF			= 1						THEN 3
																									ELSE 0
												END 
						INTO #OGRTOPLAMTASP
						FROM #Tasp				T
						JOIN #OgrenciDersMuaf	M	ON	M.TCKIMLIKNO = T.TCKIMLIKNO 
					
						SELECT	 ING = CASE	WHEN M.MUAF = 1 THEN (M.DI_OGR_TASP/@DI_T_TASP) * @I_MAX_TASP
											WHEN M.MUAF = 2 THEN (M.I_OGR_TASP / @I_T_TASP) * @I_MAX_TASP
											ELSE NULL
									   END 
