								--SELECT @MSG = 'TEKRARLANAN TC OLDUĞUNDAN DEĞERLENDİRME YAPILAMAZ KONTROL EDİNİZ!'
			
								--RAISERROR (@MSG , -- Message text.
								--	16, -- Severity.
								--	1-- State.
								--	);
								SET @HATALIDOSYAYUKLE = 1
							END
				
							DELETE FROM #OTURUMLARDOSYAYUKLE WHERE OTURUM = @SUB_OTURUMDOSYAYUKLE
						END

