					DROP TABLE IF EXISTS #TEMP2
					SELECT
						JSON_Value (j.value, '$.ID_SINAVOPTIKTEMP') as ID_SINAVOPTIKTEMP,
						JSON_Value (j.value, '$.KOLONADI') as KOLONADI, 
						JSON_Value (j.value, '$.DEGER') as DEGER
					INTO #TEMP2
