				END
				IF @ISLEM = 30	--	Öğrenci Sil
				BEGIN
					UPDATE SinavDers SET TAKMAAD=@TAKMAAD WHERE ID_SINAVDERS=@ID_SINAVDERS
					SELECT 1
				END			
				IF @ISLEM = 31  --  SınavTürü Derslerini Listele
				BEGIN	
					select(
						select * from(
							select 
							(
								SELECT DISTINCT	D.ID_DERS,D.DERSAD AD,D.ID_DERS ID
