									AND (S.DONEM		= @DONEM		OR @DONEM = '' )
								FOR JSON AUTO
							) as t1								
						) as tablo FOR JSON AUTO
					) as tt
				END 
				IF @ISLEM = 32	--	Sınav Listele Değerlendirilmiş
				BEGIN
					SELECT * FROM
					(
						SELECT DISTINCT 
							S.ID_SINAV
							,KOD
							,S.AD
							,K3.AD AS GRUP
							,S.ID_KADEME3
							,Convert(varchar, SINAVTARIH, 104) AS SINAVTARIH
							,DURUM
							,AKTIF 
							,(CASE WHEN  SK.ID_SINAV IS NULL THEN 0 ELSE 1 END ) B_KITAPCIK
