						LEFT JOIN SinavKitapcik SK ON SK.ID_SINAV=S.ID_SINAV AND SK.AD='B'
						WHERE DONEM = @DONEM and ID_SINAVTURU = @ID_SINAVTURU and S.ID_KADEME3 = @ID_KADEME3 and S.AKTIF = 1
						AND (S.OZEL=0 OR @OZELSINAVGOR=1)
					)XTABLE
