					SELECT ID_SINAVDERS, TAKMAAD, SORUNO, CEVAP, B_KARSILIK,C_KARSILIK,D_KARSILIK, KOD, ID_BILGI, ID_BILISSELSUREC, ID_SORUBANKASI
					INTO #SQLJSON 
					FROM OPENJSON(@SQLJSON)
					with
					(
						ID_SINAVDERS [int],
						TAKMAAD [varchar](MAX),
						SORUNO [int],
						CEVAP [varchar](MAX),
						B_KARSILIK [int],
						C_KARSILIK [int],
						D_KARSILIK [int],
						KOD [varchar](MAX),
						ID_BILGI INT,
						ID_BILISSELSUREC INT,
						ID_SORUBANKASI INT
