							AND (K.ID_KADEME3	= @ID_KADEME3 OR @ID_KADEME3 = 0 OR @ID_KADEME3 IS NULL )
							AND	Y.AKTIF			= 1
					ORDER BY ID_KADEME3 

					SELECT (
						SELECT *
						FROM #SINAVLISTESI
						for json path, INCLUDE_NULL_VALUES 
					)AS SINAVLISTESI

